import React from 'react';
import HealthScoreCard from './HealthScoreCard';
import DependencyTable from './DependencyTable';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { createObjectCsvWriter } from 'csv-writer';

const Dashboard = ({ results }) => {
  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.text('Dependency Health Report', 20, 10);
    const tableColumn = ['Name', 'Current', 'Latest', 'Vulns', 'Health', 'Risk'];
    const tableRows = results.map(dep => [
      dep.name,
      dep.version,
      dep.latestVersion,
      dep.vulnerabilities,
      dep.healthScore,
      dep.riskLevel
    ]);
    doc.autoTable(tableColumn, tableRows, { startY: 20 });
    doc.save('dependency-report.pdf');
  };

  const exportToCSV = () => {
    const csvWriter = createObjectCsvWriter({
      path: 'dependency-report.csv',
      header: [
        { id: 'name', title: 'Name' },
        { id: 'version', title: 'Current' },
        { id: 'latestVersion', title: 'Latest' },
        { id: 'vulnerabilities', title: 'Vulns' },
        { id: 'healthScore', title: 'Health' },
        { id: 'riskLevel', title: 'Risk' }
      ]
    });
    csvWriter.writeRecords(results).then(() => {
      alert('CSV exported successfully!');
    });
  };

  return (
    <div className="dashboard">
      <div className="export-buttons">
        <button onClick={exportToPDF} className="export-btn pdf-btn">Export to PDF</button>
        <button onClick={exportToCSV} className="export-btn csv-btn">Export to CSV</button>
      </div>
      <HealthScoreCard results={results} />
      <DependencyTable results={results} />
    </div>
  );
};

export default Dashboard;
